import { FaEllipsisV } from "react-icons/fa";
import React, { useState } from 'react';
import { Progress } from 'antd';
const Chart = () => {
  const public_key = "";
  const private_key = "";

  const [mode, setMode] = useState('C'); // C for Criptografar, D for Descriptografar
  const [message, setMessage] = useState('');
  const [result, setResult] = useState('');

  const toggleMode = () => {
    setMode((prevMode) => (prevMode === 'C' ? 'D' : 'C'));
  };

  const handleAction = () => {
    if (mode === 'C') {
      // Lógica de criptografia aqui
      setResult(btoa(message)); // Exemplo simples usando Base64
    } else {
      // Lógica de descriptografia aqui
      setResult(atob(message)); // Exemplo simples usando Base64
    }
  };


  return (
    <>
      <div className="flex mt-[22px] w-full gap-[30px]">
        <div className="basis-[55%] border bg-white shadow-md cursor-pointer rounded-[4px]">
          <div className="bg-[#F8F9FC] flex items-center justify-between py-[15px] px-[20px] border-b-[1px] border-[#EDEDED]">
            <h2 className="text-[#4e73df] text-[16px] leading-[19px] font-bold">
              Investimentos do Momento
            </h2>
            <FaEllipsisV color="gray" className="cursor-pointer" />
          </div>
          <div className="px-[25px] space-y-[15px] py-[15px]">
            <div>
              <h2>POUPANÇA</h2>
              <Progress percent={30} strokeColor="#E74A3B" />
            </div>
            <div>
              <h2>LCI/LCA</h2>
              <Progress percent={50} status="active" strokeColor="#F6C23E" />
            </div>
            <div>
              <h2>CDI</h2>
              <Progress percent={70} status="active" strokeColor="#4E73DF" />
            </div>
            <div>
              <h2>Nossa rede P2P</h2>
              <Progress percent={100} strokeColor="#36B9CC" />
            </div>
            <div>
              <h2>CRIPTOS</h2>
              <Progress percent={50} status="exception" strokeColor="#1CC88A" />
            </div>
          </div>
        </div>
        <div className="basis-[45%] border bg-white shadow-md cursor-pointer rounded-[4px]">
          <div className="bg-[#F8F9FC] flex items-center justify-between py-[15px] px-[20px] border-b-[1px] border-[#EDEDED]">
            <h2 className="text-[#4e73df] text-[16px] leading-[19px] font-bold">
              Gerencie suas chaves
            </h2>
            <FaEllipsisV color="gray" className="cursor-pointer" />
          </div>
          <div className="pl-[35px] flex items-center justify-center h-[100%]">
            <div>
              <div className="flex items-center mb-[20px]">
                <label className="mr-[10px]">Modo:</label>
                <button
                  onClick={toggleMode}
                  className="py-[5px] px-[10px] bg-[#4e73df] text-white rounded cursor-pointer"
                >
                  {mode === 'C' ? 'Criptografar' : 'Descriptografar'}
                </button>
              </div>
              <div className="mb-[20px]">
                <input
                  type="text"
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  className="py-[5px] px-[10px] border rounded w-full"
                  placeholder="Digite sua mensagem"
                />
              </div>
              <div className="mb-[20px]">
                <button
                  onClick={handleAction}
                  className="py-[5px] px-[20px] bg-[#4e73df] text-white rounded cursor-pointer"
                >
                  {mode === 'C' ? 'Criptografar' : 'Descriptografar'}
                </button>
              </div>
              <div>
                <p className="py-[5px] px-[10px] border rounded w-full bg-[#F8F9FC]">
                  Resultado: {result}
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
      </>
      );
};

      export default Chart;
